window.addEventListener("DOMContentLoaded",function(){let e=document.getElementById("contact-form"),n=document.getElementById("contact-form-button"),t=document.getElementById("contact-form-status");function s(){e.reset(),n.style="display: none ",t.innerHTML="Merci! Le formulaire de contact a été soumis avec succès."}function o(){t.innerHTML="Oops! Il y a eu un problème."}e.addEventListener("submit",function(t){t.preventDefault();let n=new FormData(e);ajax(e.method,e.action,n,s,o)})}),window.onbeforeunload=()=>{for(const e of document.getElementsByTagName("contact-form"))e.reset()};function ajax(e,t,n,s,o){let i=new XMLHttpRequest;i.open(e,t),i.setRequestHeader("Accept","application/json"),i.onreadystatechange=function(){if(i.readyState!==XMLHttpRequest.DONE)return;i.status===200?s(i.response,i.responseType):o(i.status,i.response,i.responseType)},i.send(n)}